import React, { useState, useEffect, useRef } from 'react';
import { TrendingUp, TrendingDown, ZoomIn, ZoomOut } from 'lucide-react';

const TradingSimulator = () => {
  const [balance, setBalance] = useState(10000);
  const [position, setPosition] = useState(0);
  const [avgPrice, setAvgPrice] = useState(0);
  const [currentPrice, setCurrentPrice] = useState(150);
  const [candles, setCandles] = useState([]);
  const [allCandles, setAllCandles] = useState([]);
  const [shares, setShares] = useState('');
  const [sentiment, setSentiment] = useState('neutral');
  const [history, setHistory] = useState([]);
  const [buyPrices, setBuyPrices] = useState([]);
  const [timeframe, setTimeframe] = useState(50);
  const [zoomLevel, setZoomLevel] = useState(1);
  const [leverage] = useState(20);
  const [spread] = useState(0.02);

  const sentimentRef = useRef('neutral');
  const candleTimer = useRef(null);
  const sentimentTimer = useRef(null);

  const sentiments = [
    { name: 'very_bearish', bias: -0.0008, volatility: 0.0003, upChance: 0.25 },
    { name: 'bearish', bias: -0.0004, volatility: 0.0002, upChance: 0.35 },
    { name: 'neutral', bias: 0, volatility: 0.0004, upChance: 0.5 },
    { name: 'bullish', bias: 0.0004, volatility: 0.0002, upChance: 0.65 },
    { name: 'very_bullish', bias: 0.0008, volatility: 0.0003, upChance: 0.75 }
  ];

  const getSentimentConfig = () => {
    return sentiments.find(s => s.name === sentimentRef.current) || sentiments[2];
  };

  const generateCandle = (lastClose) => {
    const config = getSentimentConfig();
    const vol = config.volatility;

    const goesUp = Math.random() < config.upChance;
    const strength = 0.3 + Math.random() * 0.7;
    const isExtremeMove = Math.random() < 0.02;
    const extremeMultiplier = isExtremeMove ? (3 + Math.random() * 4) : 1;

    let change;
    if (goesUp) {
      change = (Math.abs(config.bias) * strength + Math.random() * vol) * extremeMultiplier;
    } else {
      change = -(Math.abs(config.bias) * strength + Math.random() * vol) * extremeMultiplier;
    }

    change += (Math.random() - 0.5) * vol * 0.5;

    const open = lastClose;
    const close = open * (1 + change);

    const spread = Math.abs(close - open);
    const wickSize = spread * (0.5 + Math.random() * 1.5);

    const high = Math.max(open, close) + wickSize * Math.random();
    const low = Math.min(open, close) - wickSize * Math.random();

    return { open, high, low, close, time: Date.now() };
  };

  useEffect(() => {
    const initialCandles = [];
    let price = 150;

    for (let i = 0; i < 200; i++) {
      const candle = generateCandle(price);
      initialCandles.push(candle);
      price = candle.close;
    }

    setAllCandles(initialCandles);
    setCandles(initialCandles.slice(-timeframe));
    setCurrentPrice(price);

    candleTimer.current = setInterval(() => {
      setAllCandles(prev => {
        const lastCandle = prev[prev.length - 1];
        const newCandle = generateCandle(lastCandle.close);
        setCurrentPrice(newCandle.close);

        const updated = [...prev, newCandle];
        return updated;
      });
    }, 2000);

    const changeSentiment = () => {
      const newSentiment = sentiments[Math.floor(Math.random() * sentiments.length)].name;
      sentimentRef.current = newSentiment;
      setSentiment(newSentiment);
    };

    sentimentTimer.current = setInterval(changeSentiment, 30000);

    return () => {
      clearInterval(candleTimer.current);
      clearInterval(sentimentTimer.current);
    };
  }, []);

  useEffect(() => {
    setCandles(allCandles.slice(-timeframe));
  }, [timeframe, allCandles]);

  const buyPrice = currentPrice * (1 + spread);
  const sellPrice = currentPrice * (1 - spread);

  const buyStock = () => {
    const qty = parseInt(shares);
    if (!qty || qty <= 0) return;

    const margin = (qty * buyPrice) / leverage;
    if (margin > balance) {
      alert('Insufficient margin');
      return;
    }

    const newPosition = position + qty;
    const newAvgPrice = position === 0 ? buyPrice : ((position * avgPrice) + (qty * buyPrice)) / newPosition;

    setBalance(prev => prev - margin);
    setPosition(newPosition);
    setAvgPrice(newAvgPrice);
    setBuyPrices(prev => [...prev, buyPrice]);
    setHistory(prev => [...prev, {
      type: 'BUY',
      qty,
      price: buyPrice,
      margin: margin,
      time: new Date().toLocaleTimeString()
    }]);
    setShares('');
  };

  const sellStock = () => {
    const qty = parseInt(shares);
    if (!qty || qty <= 0) return;
    if (qty > position) {
      alert('Insufficient position');
      return;
    }

    const pnl = (sellPrice - avgPrice) * qty;
    const marginReturned = (qty * avgPrice) / leverage;

    setBalance(prev => prev + marginReturned + pnl);
    setPosition(prev => prev - qty);

    if (qty === position) {
      setAvgPrice(0);
      setBuyPrices([]);
    }

    setHistory(prev => [...prev, {
      type: 'SELL',
      qty,
      price: sellPrice,
      pnl: pnl,
      time: new Date().toLocaleTimeString()
    }]);
    setShares('');
  };

  const unrealizedPL = position > 0 ? (currentPrice - avgPrice) * position : 0;
  const usedMargin = position > 0 ? (position * avgPrice) / leverage : 0;
  const freeMargin = balance - usedMargin;
  const totalValue = balance + unrealizedPL;
  const plPercent = position > 0 ? ((currentPrice - avgPrice) / avgPrice * 100) : 0;

  const visibleCandles = candles;
  const maxPrice = Math.max(...visibleCandles.map(c => c.high));
  const minPrice = Math.min(...visibleCandles.map(c => c.low));
  const priceRange = maxPrice - minPrice;
  const padding = priceRange * 0.1;

  const baseChartMax = maxPrice + padding;
  const baseChartMin = minPrice - padding;
  const baseChartRange = baseChartMax - baseChartMin;

  const centerPrice = (baseChartMax + baseChartMin) / 2;
  const zoomedRange = baseChartRange / zoomLevel;

  const chartMax = centerPrice + (zoomedRange / 2);
  const chartMin = centerPrice - (zoomedRange / 2);
  const chartRange = chartMax - chartMin;

  const getSentimentDisplay = () => {
    const displays = {
      very_bearish: { text: 'Very Bearish', color: 'text-red-600' },
      bearish: { text: 'Bearish', color: 'text-red-500' },
      neutral: { text: 'Neutral', color: 'text-gray-600' },
      bullish: { text: 'Bullish', color: 'text-green-500' },
      very_bullish: { text: 'Very Bullish', color: 'text-green-600' }
    };
    return displays[sentiment] || displays.neutral;
  };

  const sentimentDisplay = getSentimentDisplay();

  const priceToY = (price) => {
    return ((chartMax - price) / chartRange) * 100;
  };

  const yAxisPrices = [];
  const numLabels = 8;
  for (let i = 0; i <= numLabels; i++) {
    const price = chartMin + (chartRange * i / numLabels);
    yAxisPrices.push(price);
  }

  return (
    <div className="min-h-screen bg-white p-6">
      <div className="max-w-7xl mx-auto">
        <div className="flex justify-between items-center mb-8 pb-4 border-b border-gray-200">
          <h1 className="text-2xl font-light tracking-wide">LEVERAGE TRADING SIMULATOR</h1>
          <div className="text-right">
            <div className="text-sm text-gray-500">Total Equity</div>
            <div className="text-2xl font-light">${totalValue.toFixed(2)}</div>
          </div>
        </div>

        <div className="grid grid-cols-3 gap-6 mb-8">
          <div className="border border-gray-200 p-4">
            <div className="text-xs text-gray-500 mb-1">TECH</div>
            <div className="text-3xl font-light mb-2">${currentPrice.toFixed(2)}</div>
            <div className="flex gap-4 text-sm">
              <div className="text-green-600">
                <div className="text-xs text-gray-500">BUY</div>
                <div className="font-light">${buyPrice.toFixed(2)}</div>
              </div>
              <div className="text-red-600">
                <div className="text-xs text-gray-500">SELL</div>
                <div className="font-light">${sellPrice.toFixed(2)}</div>
              </div>
            </div>
            <div className={`text-sm flex items-center gap-1 mt-2 ${currentPrice >= 150 ? 'text-green-600' : 'text-red-600'}`}>
              {currentPrice >= 150 ? <TrendingUp size={16} /> : <TrendingDown size={16} />}
              {((currentPrice - 150) / 150 * 100).toFixed(2)}%
            </div>
          </div>

          <div className="border border-gray-200 p-4">
            <div className="text-xs text-gray-500 mb-1">BALANCE</div>
            <div className="text-3xl font-light mb-2">${balance.toFixed(2)}</div>
            <div className="flex gap-4 text-sm">
              <div>
                <div className="text-xs text-gray-500">Used Margin</div>
                <div className="font-light">${usedMargin.toFixed(2)}</div>
              </div>
              <div>
                <div className="text-xs text-gray-500">Free Margin</div>
                <div className="font-light">${freeMargin.toFixed(2)}</div>
              </div>
            </div>
          </div>

          <div className="border border-gray-200 p-4">
            <div className="text-xs text-gray-500 mb-1">LEVERAGE</div>
            <div className="text-3xl font-light mb-2">1:{leverage}</div>
            <div className="text-sm">
              <div className="text-xs text-gray-500">Market Sentiment</div>
              <div className={`font-light ${sentimentDisplay.color}`}>{sentimentDisplay.text}</div>
            </div>
          </div>
        </div>

        <div className="border border-gray-200 p-6 mb-6">
          <div className="flex justify-between items-center mb-4">
            <div className="flex gap-2">
              <button
                onClick={() => setTimeframe(25)}
                className={`px-3 py-1 text-sm ${timeframe === 25 ? 'bg-gray-900 text-white' : 'bg-gray-100'}`}
              >
                25
              </button>
              <button
                onClick={() => setTimeframe(50)}
                className={`px-3 py-1 text-sm ${timeframe === 50 ? 'bg-gray-900 text-white' : 'bg-gray-100'}`}
              >
                50
              </button>
              <button
                onClick={() => setTimeframe(100)}
                className={`px-3 py-1 text-sm ${timeframe === 100 ? 'bg-gray-900 text-white' : 'bg-gray-100'}`}
              >
                100
              </button>
              <button
                onClick={() => setTimeframe(200)}
                className={`px-3 py-1 text-sm ${timeframe === 200 ? 'bg-gray-900 text-white' : 'bg-gray-100'}`}
              >
                ALL
              </button>
            </div>
            <div className="flex gap-2">
              <button
                onClick={() => setZoomLevel(prev => Math.min(prev * 1.3, 5))}
                className="p-2 border border-gray-300 hover:bg-gray-100"
              >
                <ZoomIn size={16} />
              </button>
              <button
                onClick={() => setZoomLevel(prev => Math.max(prev / 1.3, 0.5))}
                className="p-2 border border-gray-300 hover:bg-gray-100"
              >
                <ZoomOut size={16} />
              </button>
              <button
                onClick={() => setZoomLevel(1)}
                className="px-3 py-2 text-sm border border-gray-300 hover:bg-gray-100"
              >
                Reset
              </button>
            </div>
          </div>

          <div className="h-80 relative flex">
            <div className="w-16 relative border-r border-gray-200">
              {yAxisPrices.reverse().map((price, i) => (
                <div
                  key={i}
                  className="absolute right-2 text-xs text-gray-500"
                  style={{ top: `${(i / numLabels) * 100}%`, transform: 'translateY(-50%)' }}
                >
                  ${price.toFixed(2)}
                </div>
              ))}
            </div>

            <div className="flex-1 relative">
              <svg width="100%" height="100%" className="overflow-visible">
                {buyPrices.map((buyPriceVal, idx) => {
                  const y = priceToY(buyPriceVal);
                  return (
                    <g key={`buy-${idx}`}>
                      <line
                        x1="0"
                        y1={`${y}%`}
                        x2="100%"
                        y2={`${y}%`}
                        stroke="#10b981"
                        strokeWidth="1"
                        strokeDasharray="4,4"
                        opacity="0.5"
                      />
                    </g>
                  );
                })}

                {visibleCandles.map((candle, i) => {
                  const x = (i / (visibleCandles.length - 1)) * 100;
                  const candleWidth = 100 / visibleCandles.length;
                  const width = Math.max(Math.min(candleWidth * 0.7, 4), 0.5);

                  const yHigh = priceToY(candle.high);
                  const yLow = priceToY(candle.low);
                  const yOpen = priceToY(candle.open);
                  const yClose = priceToY(candle.close);

                  const isGreen = candle.close >= candle.open;
                  const color = isGreen ? '#10b981' : '#ef4444';

                  const bodyTop = Math.min(yOpen, yClose);
                  const bodyHeight = Math.max(Math.abs(yClose - yOpen), 0.3);

                  return (
                    <g key={i}>
                      <line
                        x1={`${x}%`}
                        y1={`${yHigh}%`}
                        x2={`${x}%`}
                        y2={`${yLow}%`}
                        stroke={color}
                        strokeWidth="1"
                      />
                      <rect
                        x={`${x - width/2}%`}
                        y={`${bodyTop}%`}
                        width={`${width}%`}
                        height={`${bodyHeight}%`}
                        fill={color}
                      />
                    </g>
                  );
                })}
              </svg>
            </div>
          </div>
        </div>

        <div className="grid grid-cols-2 gap-6 mb-6">
          <div className="border border-gray-200 p-6">
            <h2 className="text-sm font-light mb-4 text-gray-500">TRADE</h2>
            <div className="space-y-4">
              <input
                type="number"
                value={shares}
                onChange={(e) => setShares(e.target.value)}
                placeholder="Shares"
                className="w-full p-3 border border-gray-300 text-lg font-light focus:outline-none focus:border-gray-500"
              />
              <div className="flex gap-3">
                <button
                  onClick={buyStock}
                  className="flex-1 bg-green-600 text-white p-3 hover:bg-green-700 transition-colors font-light"
                >
                  BUY @ ${buyPrice.toFixed(2)}
                </button>
                <button
                  onClick={sellStock}
                  className="flex-1 bg-red-600 text-white p-3 hover:bg-red-700 transition-colors font-light"
                >
                  SELL @ ${sellPrice.toFixed(2)}
                </button>
              </div>
            </div>
          </div>

          <div className="border border-gray-200 p-6">
            <h2 className="text-sm font-light mb-4 text-gray-500">POSITION</h2>
            <div className="space-y-3">
              <div className="flex justify-between">
                <span className="text-gray-500">Position Size</span>
                <span className="font-light">{position} shares</span>
              </div>
              <div className="flex justify-between">
                <span className="text-gray-500">Entry Price</span>
                <span className="font-light">${avgPrice.toFixed(2)}</span>
              </div>
              <div className="flex justify-between">
                <span className="text-gray-500">Position Value</span>
                <span className="font-light">${(position * currentPrice).toFixed(2)}</span>
              </div>
              <div className="flex justify-between">
                <span className="text-gray-500">Required Margin</span>
                <span className="font-light">${usedMargin.toFixed(2)}</span>
              </div>
              <div className="flex justify-between pt-3 border-t border-gray-200">
                <span className="text-gray-500">Unrealized P/L</span>
                <span className={`font-light ${unrealizedPL >= 0 ? 'text-green-600' : 'text-red-600'}`}>
                  ${unrealizedPL.toFixed(2)} ({plPercent.toFixed(2)}%)
                </span>
              </div>
            </div>
          </div>
        </div>

        <div className="border border-gray-200 p-6">
          <h2 className="text-sm font-light mb-4 text-gray-500">TRADE HISTORY</h2>
          <div className="space-y-2 max-h-48 overflow-y-auto">
            {history.slice().reverse().map((trade, i) => (
              <div key={i} className="flex justify-between text-sm py-2 border-b border-gray-100">
                <span className={trade.type === 'BUY' ? 'text-green-600' : 'text-red-600'}>
                  {trade.type}
                </span>
                <span className="font-light">{trade.qty} @ ${trade.price.toFixed(2)}</span>
                {trade.type === 'BUY' && <span className="text-gray-500">Margin: ${trade.margin.toFixed(2)}</span>}
                {trade.type === 'SELL' && (
                  <span className={trade.pnl >= 0 ? 'text-green-600' : 'text-red-600'}>
                    P/L: ${trade.pnl.toFixed(2)}
                  </span>
                )}
                <span className="text-gray-500">{trade.time}</span>
              </div>
            ))}
            {history.length === 0 && (
              <div className="text-center text-gray-400 py-4">No trades yet</div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default TradingSimulator;
